import {
    CHANGE_SEARCHFIELD,
    REQUEST_ROBOTS_PENDING,
    REQUEST_ROBOTS_SUCCESS,
    REQUEST_ROBOTS_FAILED
} from './constants'

// A pure function always needs to return something

const intialState = {
    robots: [],
    isPending: false,
    error: '',
    searchField: ''
}

export const searchRobots = (state = intialState, action = {}) => {
    switch (action.type) {
        case CHANGE_SEARCHFIELD:
            return Object.assign({}, state.searchField, { searchField: action.payload })
        // My prefered way below
        // return { ...state, { searchField: action.payload }}
        // case FILTER_ROBOTS:
        //     return state.filter(robot => robot.name.toLowerCase().includes(action.payload.toLowerCase()))
        default:
            return state;
    }
}

export const requestRobots = (state = intialState, action = {}) => {
    switch (action.type) {
        case REQUEST_ROBOTS_PENDING:
            return Object.assign({}, state, { isPending: true });
        case REQUEST_ROBOTS_SUCCESS:
            return Object.assign({}, state.robots, { robots: action.payload, isPending: false });
        case REQUEST_ROBOTS_FAILED:
            return Object.assign({}, state.robots, { error: action.payload, isPending: false });
        default:
            return state
    }
}
